import React, {useState} from "react";
import "./Form.css";
import wyniki from "./wyniki";


function NewForm() {
  const userName = "Username";
  const age = "Age (years)";
  const [userNameValue, setUserNameValue] = useState("");
  const [userAgeValue, setUserAgeValue] = useState("");

  function userNameValueChandler(props) {
    setUserNameValue(props.target.value);
    
  }
  function userAgeValueChandler(props) {
    setUserAgeValue(props.target.value);
    
  }
  function naWyslaniu(idk){
    idk.preventDefault();
    const dane = {
      imie: {userNameValue},
      wiek: {userAgeValue}
    }
    setUserNameValue("");
    setUserAgeValue("");
    
  }

  function conlog(){
    console.log(userAgeValue);
    console.log(userNameValue);
  }

  return (
    <div>
      <div>
        <form onSubmit={naWyslaniu}>
        <h2>{userName}</h2>
        <input className="input" type="text" value= {userNameValue} onChange={userNameValueChandler}/>
        <h2>{age}</h2>
        <input className="input" type="text" value= {userAgeValue} onChange={userAgeValueChandler}/><br />
        <button type="submit" className="formButton" onClick={conlog}>
          Add user
        </button>
        </form>
      </div>

    </div>
  );
}

export default NewForm;
